import React from 'react'
import axios from 'axios';


export default class Weather_2 extends React.Component {

    constructor() {
        super()
    }

    weatherApi = async (cityName, countryName) => {
        const apiCall = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.cityName},${this.state.countryName}&APPID=${key}`)
            .then(res => {
                const data = res.data;
                console.log(data)
                this.setState({
                    temprature: res.data.main.temp
                })
            })
    }

    render() {
        return (
            <div>
                <button onClick={() => this.weatherApi()}>Click me</button>

                <h2>City is : {this.state.cityName}</h2>
                <h2>The temprature is : {this.state.temprature}</h2>
            </div>
        )
    }
}
