import React from 'react';
import axios from 'axios';

const key = 'b4a1e8d25cdbd70b76c1c854d2204b32'


export default class Weather extends React.Component {

    constructor() {
        super()
        this.state = {
            cityName: '',
            countryName: ''
        }
    }

    newCity =(event) =>{
        this.setState({
            cityName: event.target.value
        })
    }

    newCountry =(event) =>{
        this.setState({
            countryName: event.target.value
        })
    }

    weatherApi = async (cityName, countryName) => {
        const apiCall = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.cityName},${this.state.countryName}&APPID=${key}`)
        console.log(apiCall.data.temp)
    }

    render() {
        return (
            <div>
                <div>
                    <label>Enter city name</label>
                    <input type='text' value={this.state.cityName} onChange={this.newCity}/>
                </div>
                <div>
                    <label>Enter country</label>
                    <input type='text' value={this.state.countryName} onChange={this.newCountry} />
                </div>
                <button onClick={() => this.weatherApi()}>Click me</button>
            </div>
        )
    }
}
