    //     })
    // }

    weatherApi = async (cityName, countryName) => {
        const apiCall = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.cityName},${this.state.countryName}&APPID=${key}`)
            .then(res => {
                const data = res.data;
                console.log(data)
                this.setState({
                    cityName: data.name,
                    temprature: data.main.temp,


                })
            })
    }

    render() {
        return (
            <div>
                <div>
                    <label>Enter city name</label>
                    <input type='text' v placeholder={`Enter city`} />
                </div>
                <div>
                    <label>Enter country</label>
                    <input type='text'  placeholder={`Enter country`} />
                </div>
                <button onClick={() => this.weatherApi()}>Click me</button>
                <Weather2>
                city={this.state.cityName} 
                temp={this.state.temprature}
                </Weather2>
            </div>
        )
    }
}
